let projects=[{title:"Terminal IA",description:"Terminal interactiva con integración de IA para aprender historia de la programación.",image:"assets/projects/terminal-ia.jpg",tech:["JavaScript","Node.js","OpenAI API"],github:"https://github.com/laloaggro/terminal-ia",demo:"https://terminal-ia.demo.com"}];function loadProjects(){let t=document.querySelector(".projects-grid");projects.forEach(e=>{e=createProjectCard(e);t.appendChild(e)})}function createProjectCard(e){var t=document.createElement("div");return t.className="project-card",t.innerHTML=`
        <img src="${e.image}" alt="${e.title}">
        <h3>${e.title}</h3>
        <p>${e.description}</p>
        <div class="tech-stack">
            ${e.tech.map(e=>`<span>${e}</span>`).join("")}
        </div>
        <div class="project-links">
            <a href="${e.github}" target="_blank">GitHub</a>
            <a href="${e.demo}" target="_blank">Demo</a>
        </div>
    `,t}export{loadProjects};